namespace IIS.AsyncOpenXmlReportsSample.Templates.MailTemplates
{
    using System;
    using System.Collections.Generic;
    using IIS.AsyncOpenXmlReportsSample.MailConfigurations.MailComponents;
    using MimeKit;

    /// <summary>
    /// Ресурсы (изображения) для отображения в шаблоне письма.
    /// </summary>
    public static class T4MailTemplateResources
    {
        /// <summary>
        /// Получить ресурсы для полноценного отображения шаблона.
        /// </summary>
        /// <returns>Список ресурсов.</returns>
        public static List<Resource> GetT4MailTemplateResources()
        {
            Dictionary<string, string> base64codeImagesArray = new Dictionary<string, string>();
            base64codeImagesArray.Add("logoIconCid", "");
            base64codeImagesArray.Add("SocIconVkCid", "");
            base64codeImagesArray.Add("SocIconTwCid", "");
            base64codeImagesArray.Add("SocIconGhCid", "");
            base64codeImagesArray.Add("SocIconYtCid", "");
            base64codeImagesArray.Add("SocIconTgCid", "");
            ContentType contentType = new ContentType("image", "jpeg");
            List<Resource> resources = new List<Resource>();
            foreach (KeyValuePair<string, string> img in base64codeImagesArray)
            {
                byte[] bitmapData = Convert.FromBase64String(img.Value);
                resources.Add(new Resource(img.Key, bitmapData, contentType));
            }

            return resources;
        }
    }
}
